package com.fis.business.controller;import com.fis.business.service.AppParamService;import com.fis.fw.common.config.CoreConstants;import com.fis.fw.common.dto.MessagesResponse;import com.fis.fw.common.generics.controller.GenericController;import com.fis.fw.common.utils.LogUtil;import com.fis.business.entity.AppParam;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.security.access.prepost.PreAuthorize;import org.springframework.web.bind.annotation.*;import java.util.Map;@RestController@RequestMapping("/AppParam")public class AppParamController extends GenericController<AppParam, Integer> {    @Autowired    AppParamService appParamService;    @RequestMapping(value = {"/getByCodeType"}, method = RequestMethod.POST)    @PreAuthorize("@appAuthorizer.authorize(authentication, '" + CoreConstants.PRIVILEGE.VIEW + "', this)")    public @ResponseBody    ResponseEntity<?> getByCodeType(@RequestBody Map map) {        long startTime = System.currentTimeMillis();        LogUtil.showLog(logger, LogUtil.LOG_BEGIN, "getByCodeType", startTime);        MessagesResponse msg = new MessagesResponse();        try {            msg.setData(appParamService.getByCodeType(map));        } catch (Exception ex) {            logger.error(ex.getMessage(), ex);            throw ex;        } finally {            LogUtil.showLog(logger, LogUtil.LOG_END, "getByCodeType", startTime);        }        return new ResponseEntity<>(msg, HttpStatus.OK);    }}