package com.fis.business.dao.impl;import com.fis.business.dao.AppParamDao;import com.fis.fw.common.generics.impl.GenericDaoImpl;import com.fis.business.entity.AppParam;import java.util.HashMap;import java.util.List;import java.util.Map;public class AppParamDaoImpl extends GenericDaoImpl<AppParam, Integer> implements AppParamDao {    @Override    public List<AppParam> getByCodeType(Map map) {        String sql = "select APP_PARAM_ID appParamId,CODE code, TYPE type, VALUE value, STATUS status,DESCRIPTION description from ADMIN_DTHGD.APP_PARAM where" +                "(:code is null or CODE = :code)" +                "and (:type is null or TYPE =:type)" +                "and (STATUS = 'ACTIVE')";        return getByCondition(map, sql, AppParam.class);    }    @Override    public boolean existsCode(Map map) {        String sql = "select CODE from ADMIN_DTHGD.APP_PARAM where STATUS = 'ACTIVE' and upper(CODE) = upper(:code) and upper(type)=upper(:type)";        List<AppParam> lst = getByCondition(map, sql, AppParam.class);        if (lst != null && lst.size() > 0) {            return true;        } else {            return false;        }    }    @Override    public String getValue(String code, String type) {        Map map = new HashMap();        map.put("code", code);        map.put("type", type);        String sql = "select value from ADMIN_DTHGD.app_param where STATUS = 'ACTIVE' and CODE = :code and type = :type";        List<AppParam> lst = getByCondition(map, sql, AppParam.class);        if (lst != null && lst.size() > 0) {            return lst.get(0).getValue();        } else {            return "";        }    }}