package com.fis.business.service.impl;import com.fis.business.service.AppParamService;import com.fis.fw.common.generics.impl.GenericServiceImpl;import com.fis.business.entity.AppParam;import com.fis.business.repo.AppParamRepo;import org.springframework.stereotype.Service;import java.util.HashMap;import java.util.List;import java.util.Map;//import com.fis.business.dao.AppParamDao;@Servicepublic class AppParamServiceImpl extends GenericServiceImpl<AppParam, Integer> implements AppParamService {//    @Autowired//    AppParamDao appParamDao;//    public AppParamServiceImpl() {//    }//    @Autowired//    public AppParamServiceImpl(@Qualifier("AppParamDaoImpl") GenericDao<AppParam, Integer> genericDao) {//        super(genericDao);//        this.appParamDao = (AppParamDao) genericDao;//    }    @Override    public List<AppParam> getByCodeType(Map map) {        return ((AppParamRepo) getRepository()).getByCodeType(map);    }    @Override    public boolean existsCode(String code, String type) {        Map map = new HashMap();        map.put("code", code);        map.put("type", type);        return ((AppParamRepo) getRepository()).existsCode(map);    }    @Override    public String getValue(String code, String type) {        return ((AppParamRepo) getRepository()).getValue(code, type);    }}