package com.fis.fw.core.dao.impl;import org.springframework.stereotype.Repository;import com.fis.fw.common.generics.impl.GenericDaoImpl;import com.fis.fw.core.dao.LogApiDao;import com.fis.fw.core.entity.LogApi;import java.sql.Connection;import java.sql.PreparedStatement;import java.sql.Timestamp;import java.util.List;public class LogApiDaoImpl extends GenericDaoImpl<LogApi, Integer> implements LogApiDao {    @Override    public List<LogApi> save(List<LogApi> lst) throws Exception {        try (Connection conn = dataSource.getConnection()) {            conn.setAutoCommit(false);            PreparedStatement psmt = conn.prepareStatement(                    "INSERT INTO ADMIN_DTHGD.LOG_API(LOG_API_ID, " +                            " METHOD, URI, REQUEST_HEADER, REQUEST_BODY, RESPONSE_STATUS, RESPONSE_BODY, " +                            " PROCESS_TIME, CREATE_TIME, SESSION_ID, USER_ID, REQUEST_PAIR) " +                            " VALUES (ADMIN_DTHGD.LOG_API_SEQ.nextval, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)");            int count = 0;            for (LogApi item : lst) {                psmt.setObject(1, item.getMethod());                psmt.setObject(2, item.getUri());                psmt.setObject(3, item.getRequestHeader());                psmt.setObject(4, item.getRequestBody());                psmt.setObject(5, item.getResponseStatus());                psmt.setObject(6, item.getResponseBody());                psmt.setObject(7, item.getProcessTime());                psmt.setObject(8, new Timestamp(item.getCreateTime().getTime()));                psmt.setObject(9, item.getSessionId());                psmt.setObject(10, item.getUserId());                psmt.setObject(11, item.getRequestPair());                psmt.execute();                count++;                if (count % 100 == 0) {                    conn.commit();                }            }            conn.commit();        } catch (Exception ex) {            throw ex;        }        return lst;    }}